  c: {}
}
let emptyObj = Object.create(null);
deepClone(emptyObj, obj);
console.log(emptyObj.a === obj.a);
console.log(emptyObj.b === obj.b);

4、求斐波那契数列
let count = 0;
function fn(n) {
  let cache = {};
  function _fn(n) {
    if (cache[n]) {
      return cache[n]
    }
    count++
    if (n == 1 || n == 2) {
      return 1;
    }
    let prev = _fn(n - 1);
    cache[n - 1] = prev;
    let next = _fn(n - 2);
    cache[n - 2] = next;
    return prev + next
  }
  return _fn(n)
}

let count2 = 0;
function fn2(n) {
  count2++
  if (n === 1 || n === 2) {
    return 1
  }
  return fn2(n - 1) + fn2(n - 2)
}

// test
console.log(fn(20), count); // 6765 20
console.log(fn2(20), count2); / 6765 13529

其他
面试遇见不会的算法问题怎么办
揣摩面试官意图，听好关键词。比如：有序的数列做查找、要求算法复杂度是O(logN)，这类一般就是用二分的思想。
一般来说算法题目的解题思路分以下四步：

先降低数量级，拿可以计算出来的情况（数据）来构思解题步骤。
根据解题步骤编写程序，优先将特殊情况做好判断处理，比如一个大数组的问题，如果数组为两个数的长度的情况。
检验程序正确性。
是否可以优化（由浅入深），有能力的话可以故意预留优化点，这样可以体现个人技术能力。

